frappe.ui.form.on('Driver Qualification', {
  validate(frm) {
    if (frm.doc.expiry_date && frappe.datetime.get_diff(frm.doc.expiry_date, frappe.datetime.now_date()) < 0) {
      frappe.msgprint(__('License expired â€” update before using as active driver.'));
      frappe.validated = false;
    }
  }
});
